mapper.select:
   		SELECT 
			a.id id,
			a.manager_name managerName,
			a.manager_status managerStatus,
			a.manager_role managerRole
		FROM p_manager a

			a.del_flag = #{delFlag}

				AND a.manager_status = #{managerStatus}


				AND a.manager_role = #{managerRole}


			ORDER BY a.update_date DESC

   		SELECT 
			a.id id,
			a.manager_name managerName,
			a.manager_status managerStatus,
			a.manager_role managerRole,
			a.manager_area managerArea
		FROM p_manager a

			a.del_flag = #{delFlag}

				AND a.manager_status = #{managerStatus}


				AND a.manager_role = #{managerRole}


				AND a.manager_area = #{managerArea}


			ORDER BY a.update_date DESC
