mapper.select:
select 

from EDC_NEWFKTERMINAL_ORM
where MERCHANT_ID = #{merchantId,jdbcType=VARCHAR}
  and MODULE_ID = #{moduleId,jdbcType=DECIMAL}
  and TERMINAL_ID = #{terminalId,jdbcType=VARCHAR}

mapper.delete:
delete from EDC_NEWFKTERMINAL_ORM
where MERCHANT_ID = #{merchantId,jdbcType=VARCHAR}
  and MODULE_ID = #{moduleId,jdbcType=DECIMAL}
  and TERMINAL_ID = #{terminalId,jdbcType=VARCHAR}
