mapper.select:
SELECT 

FROM p_sim_act_log a

WHERE a.id = #{id}

SELECT 

FROM p_sim_act_log a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.sim_id = #{simId}


		AND a.pos_sn LIKE 
			'%'||#{posSn}||'%'
			'%'+#{posSn}+'%'
			concat('%',#{posSn},'%')


		AND a.sim_act_type = #{simActType}


		AND a.sim_act_msg LIKE 
			'%'||#{simActMsg}||'%'
			'%'+#{simActMsg}+'%'
			concat('%',#{simActMsg},'%')


		AND a.create_by = #{createBy.id}


		AND a.create_by IN

	        #{createBy.id}



		AND  to_char(a.create_date,'YYYYMMDD') >= #{beginCreateDate} 


		AND  to_char(a.create_date,'YYYYMMDD') <= #{endCreateDate} 




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM p_sim_act_log a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE p_sim_act_log SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
