mapper.select:
SELECT 

FROM sys_client a

WHERE a.id = #{id}

SELECT 

FROM sys_client a

WHERE a.clientSecret = #{clientSecret}

SELECT 

FROM sys_client a

WHERE a.clientId = #{clientId}

SELECT 

FROM sys_client a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.clientid = #{clientid}


		AND a.clientname LIKE 
			'%'||#{clientname}||'%'
			'%'+#{clientname}+'%'
			concat('%',#{clientname},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_client a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE sys_client SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
