mapper.select:
SELECT
	NOTICEID,NOTICENAME,NOTICEVALUE,CREATETIME,UPDATETIME
FROM
	NOTICE
WHERE
	1=1

		AND NOTICEID = #{noticeid}


		AND NOTICENAME = #{noticename}


		AND NOTICEVALUE = #{noticevalue}

ORDER BY UPDATETIME DESC

SELECT
	NOTICEID,NOTICENAME,NOTICEVALUE,CREATETIME,UPDATETIME
FROM
	NOTICE
WHERE
	1=1

		AND NOTICEID = #{noticeid}


		AND NOTICENAME = #{noticename}


		AND NOTICEVALUE = #{noticevalue}


		AND STATUS = #{status}


	 AND BEGINTIME <=#{time} AND  ENDTIME >= #{time}

ORDER BY UPDATETIME DESC
