mapper.insert:
insert into VIEW_STLBOOK_DETAIL (ID, DETAIL_ID, CARD_NO, 
  MER_NO, TERM_NO, TIMEZONE, 
  FEE, MER_NAME, TRAN_TYPE, 
  TRAN_DATE, TRAN_TIME, TRAN_AMT
  )
values (#{id,jdbcType=VARCHAR}, #{detailId,jdbcType=VARCHAR}, #{cardNo,jdbcType=VARCHAR}, 
  #{merNo,jdbcType=VARCHAR}, #{termNo,jdbcType=VARCHAR}, #{timezone,jdbcType=VARCHAR}, 
  #{fee,jdbcType=DECIMAL}, #{merName,jdbcType=VARCHAR}, #{tranType,jdbcType=VARCHAR}, 
  #{tranDate,jdbcType=VARCHAR}, #{tranTime,jdbcType=VARCHAR}, #{tranAmt,jdbcType=DECIMAL}
  )

mapper.select:
SELECT * FROM (
SELECT a.*, ROWNUM rn FROM(
select

	example.distinct


from VIEW_STLBOOK_DETAIL




	order by ${example.orderByClause}
)
a
WHERE  ROWNUM <= ${endResult} )
WHERE rn > ${startResult}
