mapper.select:
SELECT
FROM accdet m , crddet t
WHERE m.id=t.accdet_id AND t.pan = #{pan}

SELECT t.pan AS "pan", m.accno AS "accno", m.typecode AS "typecode", m.currcode AS "currcode", m.avlbal AS "avlbal" FROM accdet m , crddet t
m.id=t.accdet_id
AND t.pan = #{pan}
AND t.pan = ''
ORDER BY ${page.orderBy}

SELECT t.pan AS "pan", m.accno AS "accno", m.typecode AS "typecode", m.currcode AS "currcode", m.avlbal AS "avlbal" FROM accdet m , crddet t
m.id=t.accdet_id
AND t.pan = #{pan}
AND t.pan = ''
ORDER BY ${page.orderBy}

mapper.update:
		UPDATE accdet  t  set   	
			t.avlbal=#{avlbal}
		WHERE  
      t.id  =(select  m.accdet_id  from  crddet m  where  m.pan=#{pan})    and  t.typecode=#{typecode}
