SELECT 

FROM merchant_double_rate a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.merchant_id LIKE 
			'%'||#{merchantId}||'%'
			'%'+#{merchantId}+'%'
			concat('%',#{merchantId},'%')


		AND a.card_bin = #{cardBin}


		AND a.rate = #{rate}


		AND a.create_by = #{createBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

