SELECT * FROM (
SELECT c.*, ROWNUM rn FROM(
	SELECT t.cust_name ,t.cell_phone , t.id_num , t.address , t.mail_box, t.PAN 
	FROM CRDINFO t  WHERE trim(t.cell_phone) != ' ' AND t.ID IS NOT NULL

	  		AND t.CUST_NAME LIKE '%'||#{queryModel.custName}||'%'


	  		AND t.ID_NUM = #{queryModel.idNum}


	  		AND  to_char(t.date_birth,'YYYY-MM-DD') >= #{queryModel.begainTrueName} 


	  		AND  to_char(t.date_birth,'YYYY-MM-DD') <= #{queryModel.endTrueName} 

	    ORDER BY t.ID DESC) c
WHERE  ROWNUM <= #{endResult}  )
WHERE rn > #{startResult}

SELECT COUNT(t.pan) FROM CRDINFO t  WHERE trim(t.cell_phone) != ' ' AND t.ID IS NOT NULL 

	AND t.CUST_NAME LIKE '%'||#{custName}||'%'


	AND t.ID_NUM = #{idNum}


	AND  to_char(t.date_birth,'YYYY-MM-DD') >= #{begainTrueName} 


	AND  to_char(t.date_birth,'YYYY-MM-DD') <= #{endTrueName}

  	SELECT * FROM (
	SELECT t.*, ROWNUM rn FROM(
		SELECT c.cust_name ,c.cell_phone , c.id_num , c.address , c.mail_box, c.PAN 
		FROM crdinfo c
		WHERE 1=1 AND trim(c.cell_phone) != ' ' AND c.ID IS NOT NULL 

	  		AND c.CUST_NAME LIKE '%'||#{queryModel.custName}||'%'


	  		AND c.ID_NUM = #{queryModel.idNum}


	  		AND  to_char(c.date_birth,'YYYY-MM-DD') >= #{queryModel.begainTrueName} 


	  		AND  to_char(c.date_birth,'YYYY-MM-DD') <= #{queryModel.endTrueName} 


		    AND  c.id IN

	    		#{crdinfoId}


		ORDER BY c.id DESC, c.id_num DESC) t
	WHERE  ROWNUM <= #{endResult}  )
	WHERE rn > #{startResult}

	SELECT COUNT(*) FROM VIEW_CRDINFO_REAL_NAME_MBER 
  	WHERE CELL_PHONE IS NOT NULL AND CELL_PHONE != ' '

		AND  to_char(DATE_BIRTH,'YYYYMMDD') >= #{begainTrueName} 


  		AND  to_char(DATE_BIRTH,'YYYYMMDD') <= #{endTrueName} 


		AND OPERATOR = #{operator} 


		AND MEMBER_NAME = #{memberName}

  	SELECT * FROM (
	SELECT v.*, ROWNUM rn FROM(	SELECT 
	FATHER_ORDER, PAN_START, PAN_END, ID, PAN, CUST_NAME, ID_NUM, MAIL_BOX, 
	CELL_PHONE, ADDRESS, DATE_BIRTH, MEMBER_NAME FROM VIEW_CRDINFO_REAL_NAME_MBER 
	WHERE CELL_PHONE IS NOT NULL AND CELL_PHONE != ' '

		AND  to_char(DATE_BIRTH,'YYYYMMDD') >= #{queryModel.begainTrueName} 


  		AND  to_char(DATE_BIRTH,'YYYYMMDD') <= #{queryModel.endTrueName} 


		AND OPERATOR = #{queryModel.operator} 


		AND MEMBER_NAME = #{queryModel.memberName} 
	) v
	WHERE  ROWNUM <= #{endResult}  )
	WHERE rn > #{startResult}

	SELECT FATHER_ORDER, PAN_START, PAN_END, ID, PAN, CUST_NAME, ID_NUM, MAIL_BOX, 
	CELL_PHONE, ADDRESS, DATE_BIRTH, MEMBER_NAME FROM VIEW_CRDINFO_REAL_NAME_MBER 
	WHERE CELL_PHONE IS NOT NULL AND CELL_PHONE != ' '

		AND  to_char(DATE_BIRTH,'YYYYMMDD') >= #{begainTrueName} 


  		AND  to_char(DATE_BIRTH,'YYYYMMDD') <= #{endTrueName} 


		AND OPERATOR = #{operator} 


		AND MEMBER_NAME = #{memberName}

	SELECT COUNT(*) FROM (
  	SELECT CELL_PHONE FROM VIEW_CRDINFO_REAL_NAME_MBER 
	WHERE CELL_PHONE IS NOT NULL AND CELL_PHONE != ' '

		AND  to_char(DATE_BIRTH,'YYYYMMDD') >= #{begainTrueName} 


  		AND  to_char(DATE_BIRTH,'YYYYMMDD') <= #{endTrueName} 


		AND OPERATOR = #{operator} 


		AND MEMBER_NAME = #{memberName} 

	GROUP BY CELL_PHONE)
