mapper.select:
SELECT * FROM (
SELECT c.*, ROWNUM rn FROM(
select

	example.distinct


from T_CLEAR_MERSTL_FINAL_BOOK_T a




	order by ${example.orderByClause}
)
c
WHERE  ROWNUM <= ${endResult} )
WHERE rn > ${startResult}

select a.province_code, a.province_name, a.city_code, a.city_name, sum(a.consum_num) as consum_num, to_char(sum(a.consum_amt), 'fm999999999999990.90') as consum_amt, to_char(sum(a.fee), 'fm999999999999990.90') as fee, to_char(sum(a.net_amt), 'fm999999999999990.90') as net_amt, to_char(sum(a.tran_amt), 'fm999999999999990.90') as tran_amt from T_CLEAR_MERSTL_FINAL_BOOK_T a
group by province_code, province_name, city_code, city_name
order by ${example.orderByClause}
