mapper.select:
		SELECT COUNT(*) FROM (SELECT

		from (SELECT
			 a.MER_NO,a.TRAN_NUM, 
          	 a.TRAN_AMT,b.PROVINCE_NAME, b.CITY_NAME
          	 FROM T_CLEAR_MERCLEAR_BOOK  a 
          	 LEFT JOIN VIEW_FKCORE_MER_BASE b ON a.mer_no = b.MRCHNO
			 WHERE 1=1

      		 	AND   a.TRAN_DATE >= #{queryModel.startStlDate}  


      		 	AND   a.TRAN_DATE <= #{queryModel.endStlDate} 


      		 	AND a.MER_NAME LIKE '%'||#{queryModel.merName}||'%'

		) WHERE 1=1 

      		AND MER_NO = #{queryModel.merNo}


      		AND PROVINCE_NAME = #{queryModel.provinceName}


      		AND CITY_NAME = #{queryModel.cityName}

		GROUP BY mer_no, province_name, city_name
		HAVING 1=1 

      		AND   SUM(TRAN_NUM) >= #{queryModel.consumNum} 


      		AND   SUM(TRAN_AMT) >= #{queryModel.tranAmt} 
    	)

		SELECT * FROM (
		SELECT t.*, ROWNUM rn FROM(
		SELECT * FROM (SELECT

		from (SELECT
			 a.MER_NO,a.TRAN_NUM, 
          	 a.TRAN_AMT,b.PROVINCE_NAME, b.CITY_NAME
          	 FROM T_CLEAR_MERCLEAR_BOOK  a 
          	 LEFT JOIN VIEW_FKCORE_MER_BASE b ON a.mer_no = b.MRCHNO
			 WHERE 1=1

      		 	AND   a.TRAN_DATE >= #{queryModel.startStlDate}  


      		 	AND   a.TRAN_DATE <= #{queryModel.endStlDate} 


      		 	AND a.MER_NAME LIKE '%'||#{queryModel.merName}||'%'

		) WHERE 1=1 

      		AND MER_NO = #{queryModel.merNo}


      		AND CITY_NAME = #{queryModel.cityName}


      		AND PROVINCE_NAME = #{queryModel.provinceName}

		GROUP BY mer_no, province_name, city_name
		HAVING 1=1 

      		AND   SUM(TRAN_NUM) >= #{queryModel.consumNum} 


      		AND   SUM(TRAN_AMT) >= #{queryModel.tranAmt} 
    	 ) 
		ORDER BY TRAN_AMT DESC, MER_NO DESC,province_name DESC,city_name DESC
		) t  WHERE  ROWNUM <= #{endResult}  )
		WHERE rn > #{startResult}

		SELECT * FROM (SELECT

		from (SELECT
			 a.MER_NO,a.TRAN_NUM, 
          	 a.TRAN_AMT,b.PROVINCE_NAME, b.CITY_NAME
          	 FROM T_CLEAR_MERCLEAR_BOOK  a 
          	 LEFT JOIN VIEW_FKCORE_MER_BASE b ON a.mer_no = b.MRCHNO
			 WHERE 1=1

      		 	AND   a.TRAN_DATE >= #{queryModel.startStlDate}  


      		 	AND   a.TRAN_DATE <= #{queryModel.endStlDate} 


      		 	AND a.MER_NAME LIKE '%'||#{queryModel.merName}||'%'

		) WHERE 1=1 

      		AND MER_NO = #{queryModel.merNo}


      		AND CITY_NAME = #{queryModel.cityName}


      		AND PROVINCE_NAME = #{queryModel.provinceName}

		GROUP BY mer_no, province_name, city_name
		HAVING 1=1 

      		AND   SUM(TRAN_NUM) >= #{queryModel.consumNum} 


      		AND   SUM(TRAN_AMT) >= #{queryModel.tranAmt} 
    	 ) 
		ORDER BY TRAN_AMT DESC, MER_NO DESC,province_name DESC,city_name DESC

SELECT DISTINCT MER_NO AS merNo FROM T_CLEAR_MERSTL_BOOK  
WHERE  STL_DATE >= #{startStlDate}  
