mapper.select:
SELECT 

FROM CRDMEMBER a 

WHERE MEMBER_ID = #{memberId,jdbcType=VARCHAR}

SELECT 

FROM CRDMEMBER a 

WHERE 1=1 

	AND a.MEMBER_NAME = #{memberName}


	AND a.CARD_BIN = #{cardBin}

mapper.update:
update CRDMEMBER
set MEMBER_NAME = #{memberName,jdbcType=VARCHAR},
  CARD_BIN = #{cardBin,jdbcType=VARCHAR},
  UPDATE_TIME = #{updateTime,jdbcType=VARCHAR},
  UPDATE_BY = #{updateBy.loginName,jdbcType=VARCHAR}
where MEMBER_ID = #{memberId,jdbcType=VARCHAR}
