mapper.select:
SELECT g.GAMEID GAMEID, c.PLAYNAME PLAYNAME, g.TMPTYPEID TMPTYPEID, g.TMPTYPENAME TMPTYPENAME FROM GAMETEMPLATETYPE g LEFT JOIN CLOUDGAMEINIT c ON g.GAMEID = c.ID WHERE ( 1=1
AND g.TMPTYPEID=#{tmptypeid}
AND g.TMPTYPENAME=#{tmptypename}
AND g.GAMEID=#{gameid}
) ORDER BY g.TMPTYPEID DESC

SELECT 
	GAMEID,
	TMPTYPEID,
	TMPTYPENAME 
FROM 
	GAMETEMPLATETYPE
WHERE
	(
	1=1 
		AND TMPTYPEID != #{tmptypeid}  
		AND TMPTYPENAME = #{tmptypename}  
		AND GAMEID = #{gameid}  
	)

mapper.update:
UPDATE
	GAMETEMPLATETYPE
SET
	TMPTYPENAME=#{tmptypename}
WHERE
	TMPTYPEID=#{tmptypeid}
