mapper.select:
SELECT
		GAMEID,
		GAMENAME
FROM
		CLOUDGAMEPARAM
WHERE 	1=1
		AND APPID = #{appid}
		AND CHANNEL = #{channel}
		AND STATUS = #{status}
ORDER BY UPDATETIME DESC

SELECT
		GAMEID,
		GAMENAME
FROM
		CLOUDGAMEPARAM
WHERE 	1=1
		AND ORGID = #{orgid}
		AND CHANNEL = #{channel}
		AND STATUS = #{status}
ORDER BY UPDATETIME DESC
